import { ScreenMask } from './fgta_screenmask.mjs'

export var Messager = {
	Dialog: Dialog,
	Warning: Warning,
	Error: Error,
	Info: Info,
	Question: Question,
	Wait: Wait,
	Debug: Debug
}


var counter = 0;

function Warning(message, buttons) {
	return Dialog(message, buttons, function(elmask) {
		var head = document.createElement('div');
		head.innerHTML = `<img src="${img_warning}" width="${image_size}" height="${image_size}">`;
		head.style.cssText = get_css_head48();
		return head;
	})
}

