function Dialog(message, buttons, fn_createheader) {
	return Show({
		fn_createheader: function(elmask) {
			if (typeof fn_createheader === 'function') {
				return fn_createheader(elmask)
			}
		},
		fn_createcontent: function(elmask) { return Dialog_CreateContent(elmask, message) },
		fn_createfooter: function(elmask) { return Dialog_CreateFooter(elmask, buttons) },
		fn_showed: function(elmask) { Dialog_ButtonFocus(elmask) }
	});
}


function isHTML(str) {
	var a = document.createElement('div');
	a.innerHTML = str;
  
	for (var c = a.childNodes, i = c.length; i--; ) {
	  if (c[i].nodeType == 1) return true; 
	}
  
	return false;
}

function Debug(message, title) {
	var maskid = 'fgtamask-debug-00';
	var elmask = ScreenMask.Create(maskid);

	var head;
	if (title!==undefined) {
		head = document.createElement('div')
		head.innerHTML = title;
		head.style.cssText = `
			margin: 10px 5px 10px 5px;
			font-weight: bold;
			font-size: 1.2em;
		`;
	}

	var content = document.createElement('div');
	if (isHTML(message)) {
		content.innerHTML = message;
	} else {
		content.innerHTML = '<pre>' + message + '</pre>';
	}
	
	content.style.cssText = `
		display: flex;
		flex: 1;
		border: 1px solid #ccc;
		overflow: scroll;
		margin: 5px;
		background-color: #eee;
		border-radius: 3px;
	`;

	var debugdialog = CreateDialog(
		head,
		content,
		Dialog_CreateFooter(elmask)
	);
	debugdialog.style.width = '100vw';
	debugdialog.style.height = '100vh';
	debugdialog.style.margin = '1px';	

	elmask.appendChild(debugdialog);
	return elmask;
}



function Dialog_CreateContent(elmask, message) {
	var content = document.createElement('div');
	content.innerHTML = message;
	content.style.cssText = get_css_content();
	return content;
}

function Dialog_CreateFooter(elmask, buttons) {
	if (buttons===undefined) {
		buttons = {
			'Ok': function(elmask) {
				elmask.Close();
			}
		}
	}

	var footer = document.createElement('div');
	footer.style.cssText = get_css_footer();

	var n=0;
	for (var btnid in buttons) {
		++n;
		var btnfn = buttons[btnid];
		var btn = document.createElement('button');
		btn.id = elmask.id + '-btn-' + n;
		btn.appendChild(document.createTextNode(btnid));
		if (typeof btnfn === 'function') {
			btn.addEventListener('click', function() {
				btnfn(elmask);
			});
		}
		footer.appendChild(btn);
		elmask.DefaultButton = btn;
	}

	return footer;
}


function Dialog_ButtonFocus(elmask) {
	var btn = document.getElementById(elmask.DefaultButton.id);
	setTimeout(function() {
		btn.focus();
	}, 200);
}

function Show(options) {
	++counter;
	var maskid = 'fgtamask-i-' + counter;
	var elmask = ScreenMask.Create(maskid);
	var dialog = CreateDialog(
		options.fn_createheader(elmask), 
		options.fn_createcontent(elmask), 
		options.fn_createfooter(elmask)
	);
	elmask.appendChild(dialog);
	if (typeof options.fn_showed === 'function') {
		options.fn_showed(elmask);
	}
	return elmask;
}


function Wait(waitmessage) {
	var maskid = 'fgtamask-waite-00';
	var elmask;
	
	if (waitmessage===false) {
		elmask = document.getElementById(maskid);
		if (elmask!=null) {
			elmask.style.display = 'none';
		}
	} else {
		if (waitmessage===undefined || waitmessage===null || waitmessage===true || waitmessage==="") waitmessage = 'wait...';
		setTimeout(function() {
			document.activeElement.blur();
		}, 200);
		elmask = ScreenMask.Create(maskid);
		if (elmask.dialog===undefined) {
			var head = document.createElement('div');
			head.innerHTML = `<img src="${img_wait}" width="80px" height="80px">`;
			head.style.cssText = `
				display: flex;
				width: 100%;
				justify-content: center;
				align-items: center;
				margin-top: 30px;
				margin-bottom: 0px;
				height: 80px;			
			`;
			elmask.dialog = CreateDialog(
				head,
				Dialog_CreateContent(elmask, waitmessage)
			);
			elmask.appendChild(elmask.dialog);
		}
		elmask.style.display = 'flex';
		
	}

	return elmask;
}


function CreateDialog(header, content, footer) {
	var dialog = document.createElement('div');
	dialog.style.cssText = get_css_dialog();
	if (header!=null) { dialog.appendChild(header); }
	if (content!=null) { dialog.appendChild(content); }	
	if (footer!=null) { dialog.appendChild(footer); }	

	return dialog;
}


function get_css_head48() {
	return `
		display: flex;
		width: 100%;
		justify-content: center;
		align-items: center;
		margin-top: 10px;
		margin-bottom: 10px;
		height: 48px;
	`;
}

function get_css_dialog() {
	return `
		background-color: white;
		color: black;
		min-width: 350px;
		min-height: 200px;
		display: flex;
		flex-direction: column;
		justify-content: space-between;
		border-radius: 3px;
		-webkit-box-shadow: 0px 0px 20px 0px rgba(0,0,0,0.75);
		-moz-box-shadow: 0px 0px 20px 0px rgba(0,0,0,0.75);
		box-shadow: 0px 0px 20px 0px rgba(0,0,0,0.75);		
	`;
}

function get_css_content() {
	return `
		display: flex;
		width: 100%;
		justify-content: center;
		align-items: center;
		flex: 1;
	`;
}

function get_css_footer() {
	return `
		display: flex;
		width: 100%;
		justify-content: center;
		align-self: flex-end;
		margin-top: 5px;
		margin-bottom: 5px;		
	`;
}

const defaultWidth = '350px';
const defaultHeight = '200px';
const image_size = '48px';
const img_warning = "data:image/png;base64,";
const img_error = "data:image/png;base64,";
const img_info = "data:image/png;base64,";
const img_question = "data:image/png;base64,";
const img_wait = "data:image/gif;base64,";
